/**
 * Greets the user with a message.
 * @param {string} name - The name of the user.
 */
function greet(name) {
    // Use template literals for consistent formatting
    console.log(`Hello, ${name}`);
}

// Call the function with a proper argument
greet('Juan');

// Example of consistent spacing and indentation
const numbers = [1, 2, 3, 4, 5];

numbers.forEach((number) => {
    // Use consistent semi-colons and spacing
    console.log(number);
});
